function dt(e) {
    return e.replace(/(\[.*?\])/gi, "")
}

function rutgon(e,t){for(var n=e.split("<"),r=0;r<n.length;r++)-1!=n[r].indexOf(">")&&(n[r]=n[r].substring(n[r].indexOf(">")+1,n[r].length));n=n.join("");return n.substring(0,t-1)}


function doHighlight(a,c,f){for(var d="",b=-1,g=c.toLowerCase(),e=a.toLowerCase();0<a.length;)b=e.indexOf(g,b+1),0>b?(d+=a,a=""):a.lastIndexOf(">",b)>=a.lastIndexOf("<",b)&&e.lastIndexOf("/script>",b)>=e.lastIndexOf("<script",b)&&(d+=a.substring(0,b)+'<a title="Phim '+a.substr(b,c.length)+'" href="'+f+'">'+a.substr(b,c.length)+"</a>",a=a.substr(b+c.length),e=a.toLowerCase(),b=-1);return d} function highlightSearchTerms(a,c,f,d){searchArray=c?[a]:a.split(" ");div=document.getElementById(d);a=div.innerHTML;for(c=0;c<searchArray.length;c++)a=doHighlight(a,searchArray[c],f);div.innerHTML=a;return!0};



(function($) {
	var $form = $('#ajax-search-form'),
		$input = $form.find(':text');
	$form.append('<div class="search-item"></div>');
	var $result_container = $('.search-item');
	$form.on("submit", function() {
		var keyword = $input.val();
      $result_container.show().html('<li class="key">Đang tìm kiếm...</li>');
		$.ajax({
			url: ''+siteUrl+'/feeds/posts/summary?alt=json-in-script&q=' + keyword + '&max-results=9999',
			type: 'get',
			dataType: 'jsonp',
			success: function(json) {
				var entry = json.feed.entry,
					posturl, posttitle, skeleton, thumburl = "";
				if (entry !== undefined) {
					skeleton = '<li class="key"><a href="/p/archiver.html?q=' + keyword + '">Tìm kiếm với &quot;' + keyword + '&quot; (more)</a></li>';
					skeleton += '<a class="close" href="/">&times;</a><ol>';
					 for (var i = 0; i < entry.length; i++) {
                      for (var j=0; j < entry[i].link.length; j++)
                      {
                        if (entry[i].link[j].rel == "alternate")
                        {
                          posturl = entry[i].link[j].href;
                          break;
                        }
                      }

                      ("media$thumbnail" in entry[i])?thumburl = entry[i].media$thumbnail.url:thumburl = '';
                      thumburl = thumburl.replace(/s72-c/gi,'s150');

                      posttitle = entry[i].title.$t;


if(posttitle.indexOf('-') != -1){
		var tit_vi = dt(posttitle.split('-')[0]);
		var tit_en = dt(posttitle.split('-')[1]);
	}else{tit_vi = dt(posttitle); tit_en = "Đang cập nhật...";}



                       skeleton += '<li><a title="'+posttitle+'" href="' + posturl + '"><img src="'+ thumburl +'" width="45" height="65"/>'+tit_vi+'<br/><p>'+tit_en+'</p><br class="clr"/></a></li>';
					}
					skeleton += '</ol>';
					$result_container.html(skeleton);
				} else {
					$result_container.html('<a class="close" href="/">&times;</a><li class="key">Không có kết quả. gõ từ cụ thể hơn!</li>');
				}
			},
			error: function() {
				$result_container.html('<a class="close" href="/">&times;</a><li class="key">Error loading feed.</li>');
			}
		});
		return false;
	});
	$form.on("click", ".close", function() {
		$result_container.fadeOut();
		return false;
	});
})(jQuery);


// Feed configuration
	maxResults = 10,
	containerId = 'random-post-container';

// Function to generate random number limited from `min` to `max`
// Used to create a valid and safe random feed `start-index`
function getRandomInt(min, max) {
	return Math.floor(Math.random() * (max - min + 1)) + min;
}

// Function to shuffle arrays
// Used to randomize order of the generated JSON feed
function shuffleArray(arr) {
	var i = arr.length, j, temp;
	if (i === 0) return false;
	while (--i) {
		j = Math.floor(Math.random() * (i + 1));
		temp = arr[i];
		arr[i] = arr[j]; 
		arr[j] = temp;
	}
	return arr;
}

// Get a random start index
function createRandomPostsStartIndex(json) {
	var startIndex = getRandomInt(1, (json.feed.openSearch$totalResults.$t - maxResults));
	if (window.console && window.console.log) console.log('Get the post feed start from ' + startIndex + ' until ' + (startIndex + maxResults)); 
	document.write('<scr' + 'ipt src="/feeds/posts/summary?alt=json-in-script&orderby=updated&start-index=' + startIndex + '&max-results=' + maxResults + '&callback=randomPosts"></scr' + 'ipt>');
}

// Widget's main function
function randomPosts(json) {
	var posturl, posttitle, ct = document.getElementById(containerId),
		entry = shuffleArray(json.feed.entry),
		skeleton = "<ul class='list list-play'>";

	for (var i = 0; i < entry.length; i++) {
                      for (var j=0; j < entry[i].link.length; j++)
                      {
                        if (entry[i].link[j].rel == "alternate")
                        {
                          posturl = entry[i].link[j].href;
                          break;
                        }
                      }

              posttitle = entry[i].title.$t;
                      if(posttitle.indexOf('(') != -1 || posttitle.indexOf(')') != -1){ Ntitle = posttitle.split('(')[0]; Etitle = posttitle.split('(')[1].split(')')[0]; var tit_vi = dt(posttitle.split('-')[0]);
                      }

var xrandom = Math.random();
views =Math.round(xrandom*900);

		skeleton += '<li><a href="' + posturl + '" title="'+posttitle+'">' + rutgon(Ntitle,30) + '...</a><span class="status">'+views+'</span></li>';
	}
	ct.innerHTML = skeleton + '</ul>';
}
